    static ArrayList<Info> getListData() {
        ArrayList<Info> list = new ArrayList<>();
        for (int position = 0; position < infoNames.length; position++) {
            Info hero = new Info();
            hero.setName(infoNames[position]);
            hero.setDetail(infoDetails[position]);
            hero.setShortDescription(shortDescriptions[position]);  // Menetapkan deskripsi singkat
            hero.setPhoto(infoImages[position]);
            list.add(hero);
        }
        return list;
    }
